import React from 'react';
import { Link } from 'react-router-dom';
import { useQuery, gql } from '@apollo/client';

const GET_FARMS_COUNT = gql`
  query GetFarmsCount {
    farms {
      _id
    }
  }
`;

const LandingPage = () => {
  const { data, loading, error } = useQuery(GET_FARMS_COUNT);
  
  const farmsCount = data?.farms?.length || 0;

  return (
    <div className="container">
      <div style={{ textAlign: 'center', margin: '2rem 0 4rem' }}>
        <h1>Welcome to On The Table</h1>
        <p style={{ fontSize: '1.25rem', maxWidth: '800px', margin: '1.5rem auto' }}>
          Discover local farms, explore sustainable agriculture, and connect with 
          farmers in your community.
        </p>
        
        {!loading && !error && (
          <p>Currently featuring {farmsCount} farms in our database.</p>
        )}
        
        <Link to="/map" className="btn" style={{ marginTop: '1.5rem' }}>
          Explore Farm Map
        </Link>
      </div>
      
      <div className="grid" style={{ marginTop: '3rem' }}>
        <div className="card">
          <h3>Find Local Farms</h3>
          <p>
            Use our interactive map to discover farms in your area. Filter by crop type,
            location, and more.
          </p>
        </div>
        
        <div className="card">
          <h3>Farm Information</h3>
          <p>
            Get detailed information about each farm, including what they grow, 
            location, and contact information.
          </p>
        </div>
        
        <div className="card">
          <h3>Support Local Agriculture</h3>
          <p>
            Connect with local farmers and support sustainable agriculture 
            in your community.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
