import React from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useQuery, gql } from '@apollo/client';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const GET_FARM = gql`
  query GetFarm($id: ID!) {
    farm(id: $id) {
      _id
      name
      description
      location {
        coordinates
      }
      acreage
      crops
      owner
      contact
      website
      imageUrl
    }
  }
`;

const FarmDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { loading, error, data } = useQuery(GET_FARM, {
    variables: { id },
    onError: () => {
      // Redirect to 404 if farm not found
      navigate('/404');
    }
  });

  if (loading) return <div className="container"><p>Loading farm details...</p></div>;
  if (error) return <div className="container"><p>Error: {error.message}</p></div>;
  if (!data?.farm) return <div className="container"><p>Farm not found</p></div>;

  const { farm } = data;
  
  // Check if we have valid coordinates
  const hasValidCoordinates = farm.location?.coordinates && 
    Array.isArray(farm.location.coordinates) && 
    farm.location.coordinates.length === 2;
  
  // GeoJSON format is [longitude, latitude], but Leaflet needs [latitude, longitude]
  const position = hasValidCoordinates 
    ? [farm.location.coordinates[1], farm.location.coordinates[0]]
    : [0, 0]; // Default position if no coordinates

  return (
    <div className="container">
      <Link to="/map" className="btn btn-secondary" style={{ marginBottom: '1rem' }}>
        &larr; Back to Map
      </Link>
      
      <div className="farm-details">
        <h1>{farm.name}</h1>
        
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem', marginBottom: '2rem' }}>
          {/* Left column */}
          <div style={{ flex: '1 1 500px' }}>
            {farm.imageUrl && (
              <img 
                src={farm.imageUrl} 
                alt={farm.name} 
                style={{ 
                  width: '100%', 
                  maxHeight: '300px', 
                  objectFit: 'cover', 
                  borderRadius: '4px',
                  marginBottom: '1.5rem'
                }}
              />
            )}
            
            {farm.description && (
              <div className="farm-description">
                <h2>About</h2>
                <p>{farm.description}</p>
              </div>
            )}
            
            <div className="farm-details-grid" style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
              gap: '1.5rem',
              marginTop: '1.5rem'
            }}>
              {farm.crops?.length > 0 && (
                <div>
                  <h3>Crops</h3>
                  <p>{farm.crops.join(', ')}</p>
                </div>
              )}
              
              {farm.acreage && (
                <div>
                  <h3>Farm Size</h3>
                  <p>{farm.acreage} acres</p>
                </div>
              )}
              
              {farm.owner && (
                <div>
                  <h3>Owner</h3>
                  <p>{farm.owner}</p>
                </div>
              )}
            </div>
            
            <div className="contact-info" style={{ marginTop: '2rem' }}>
              <h2>Contact Information</h2>
              
              {farm.contact && (
                <p>
                  <strong>Contact:</strong> {farm.contact}
                </p>
              )}
              
              {farm.website && (
                <p>
                  <strong>Website:</strong>{' '}
                  <a 
                    href={farm.website.startsWith('http') ? farm.website : `https://${farm.website}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {farm.website}
                  </a>
                </p>
              )}
            </div>
          </div>
          
          {/* Right column - Map */}
          <div style={{ flex: '1 1 400px' }}>
            {hasValidCoordinates ? (
              <div style={{ height: '400px', borderRadius: '4px', overflow: 'hidden' }}>
                <MapContainer 
                  center={position} 
                  zoom={13} 
                  style={{ height: '100%', width: '100%' }}
                >
                  <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  />
                  <Marker position={position}>
                    <Popup>
                      <strong>{farm.name}</strong>
                    </Popup>
                  </Marker>
                </MapContainer>
              </div>
            ) : (
              <div className="card">
                <p>No location data available for this farm.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FarmDetailsPage;
